IF OBJECT_ID('dbo.POST', 'U') IS NOT NULL 
BEGIN
  DROP TABLE POST; 
END

IF OBJECT_ID('dbo.ACCOUNT', 'U') IS NOT NULL 
BEGIN
  DROP TABLE ACCOUNT; 
END

CREATE TABLE ACCOUNT (

	accId				int				NOT NULL IDENTITY,
	accFirstName		varchar(50)		NOT NULL,
	accLastName			varchar(50)		NOT NULL,
	accEmail			varchar(150)	NOT NULL,
	accPassword			varchar(50) 	NOT NULL,
	accRegistrationDate datetime2(7)	NOT NULL DEFAULT CURRENT_TIMESTAMP,
	accSchool			varchar(100),
	accYear				int	,
	accStudy			varchar(100),
	accBudget			decimal(8, 2),
	accActive			bit				NOT NULL DEFAULT 1,

	CONSTRAINT pk_ACCOUNT_accId PRIMARY KEY(accId)

);

CREATE TABLE dbo.POST (

	postId			int				NOT NULL IDENTITY,
	postBody		varchar(140)	NOT NULL,
	postTag			varchar(50)	,
	postURL			varchar(150),
	postThumbsUp	int				NOT NULL DEFAULT 0,
	postThumbsDown	int				NOT NULL DEFAULT 0,
	postDate		datetime2(7)	NOT NULL DEFAULT CURRENT_TIMESTAMP,
	accId			int				NOT NULL,

	CONSTRAINT PK_POST_postId PRIMARY KEY(postId),
	CONSTRAINT FK_POST_ACCOUNT FOREIGN KEY(accId) REFERENCES ACCOUNT(accId)

);


